if pac then   	include("fishing/init.lua")else	hook.Add("pac_Initialized", "fishing_mod_init", function() 		include("fishing/init.lua")		hook.Remove("pac_Initialized", "fishing_mod_init")	end)end	if SERVER then	local function add_files(dir)		local files, folders = file.Find(dir .. "*", "LUA")				for key, file_name in pairs(files) do			AddCSLuaFile(dir .. file_name)		end	end		add_files("fishing/")	add_files("fishing/bait/")	add_files("fishing/fish/")	add_files("fishing/client/")	add_files("fishing/libraries/")	add_files("fishing/client/pac_outfits/")		resource.AddFile("materials/fishing_mod/inventory.png")	resource.AddFile("materials/fishing_mod/inventory_top.png")end